code.installcode:


					// Make sure the product exists
					if (!file_exists(DIR . '/'. $vbulletin->config['Misc']['admincpdir'] .'/oneallsociallogin.php'))
					{
						print_dots_stop();
						print_cp_message('Please upload the files that came with <a href="http://docs.oneall.com/plugins/guide/social-login-vbulletin/4/" target="_blank">OneAll Social Login</a> before installing or upgrading!');					
					}

					// User Mapping
					// http://docs.oneall.com/api/resources/users/
					$sql = "CREATE TABLE IF NOT EXISTS `". TABLE_PREFIX ."oasl_user` (
			 			`oasl_userid` int(10) unsigned NOT NULL AUTO_INCREMENT, 
			 			`userid` int(10) unsigned NOT NULL, 
			 			`user_token` varchar(36) NOT NULL, 
			 			`date_created` int(10) unsigned NOT NULL, 
			 			PRIMARY KEY (`oasl_userid`), 
			 			UNIQUE KEY `user_token` (`user_token`), 
			 			UNIQUE KEY `userid` (`userid`)
			 		)";
             		$vbulletin->db->query_write($sql);

             		// Identity Mapping
             		// http://docs.oneall.com/api/resources/identities/
             		$sql = "CREATE TABLE IF NOT EXISTS `". TABLE_PREFIX ."oasl_identity` (
 						`oasl_identityid` int(10) unsigned NOT NULL AUTO_INCREMENT,
						`oasl_userid` int(10) unsigned NOT NULL,
						`identity_token` varchar(36) NOT NULL,
						`identity_provider` varchar(64) NOT NULL,
						`num_logins` smallint(5) unsigned NOT NULL DEFAULT '0',
						`date_added` int(10) unsigned NOT NULL,
						`date_updated` int(10) unsigned NOT NULL,
						PRIMARY KEY (`oasl_identityid`),
						UNIQUE KEY `oaid` (`oasl_identityid`)
			 		)";
             		$vbulletin->db->query_write($sql);

             		// Sessions
             		$sql = "CREATE TABLE IF NOT EXISTS `". TABLE_PREFIX ."oasl_session` (
						`oasl_sessionid` int(10) unsigned NOT NULL AUTO_INCREMENT,
						`userid` int(10) unsigned NOT NULL,
						`session_token` varchar(36) NOT NULL,
						`date_created` int(10) unsigned NOT NULL,
						PRIMARY KEY (`oasl_sessionid`),
						UNIQUE KEY `user_token` (`session_token`),
						UNIQUE KEY `userid` (`userid`)
					)";
             		$vbulletin->db->query_write($sql);

code.uninstallcode:


					$sql = ""DROP TABLE IF EXISTS `" . TABLE_PREFIX . "oasl_user`";
					$vbulletin->db->query_write($sql);

               		$sql = ""DROP TABLE IF EXISTS `" . TABLE_PREFIX . "oasl_identity`";
